import React, { useState } from 'react';
import './Calender.css';
import axios, { AxiosResponse, AxiosRequestConfig, RawAxiosRequestHeaders } from 'axios';

const backend = axios.create({
  baseURL: '127.0.0.1:8000'
})

type Day = {
  date: Date;
  isCurrentMonth: boolean;
};

const getMonthDays = (year: number, month: number): Day[] => {
  const days: Day[] = [];

  const firstDayOfMonth = new Date(year, month, 1);
  const startDay = new Date(firstDayOfMonth);
  startDay.setDate(firstDayOfMonth.getDate() - firstDayOfMonth.getDay());

  for (let i = 0; i < 42; i++) {
    const currentDate = new Date(startDay);
    currentDate.setDate(startDay.getDate() + i);

    days.push({
      date: currentDate,
      isCurrentMonth: currentDate.getMonth() === month,
    });
  }

  return days;
};

const getMonthName = (month: number): string => {
  return [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December',
  ][month];
};

const getWeekdayNames = (): string[] => {
  return ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
};

const Calendar: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [notes, setNotes] = useState("");
  const [selectedDate, setSelectedDate] = useState(new Date());

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const days = getMonthDays(year, month);

  const goToPreviousMonth = () => {
    const prevMonth = new Date(year, month - 1, 1);
    setCurrentDate(prevMonth);
  };

  const goToNextMonth = () => {
    const nextMonth = new Date(year, month + 1, 1);
    setCurrentDate(nextMonth);
  };

  
  const getDaysInMonth = (year: number, month: number) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year: number, month: number) => {
    return new Date(year, month, 1).getDay();
  };
  
  const isToday = (date: Date) => {
    const today = new Date();
    return (
      date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear()
    );
  };


  

  const renderCells = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDayOfMonth = getFirstDayOfMonth(year, month);

    const blanks = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
      blanks.push(<div key={`blank-${i}`} className="empty-cell"></div>);
    }

    const days = [];
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
    
      days.push(
        <div
          key={`day-${day}`}
          className={`calendar-cell ${isToday(date) ? 'today' : ''}`}
          // onClick={() => onDateClick && onDateClick(date)}
        >
          <div className="day-number">{day}</div>
          
        </div>
      );
    }

    console.log('days')
    console.log(days)  


    const totalCells = [...blanks,...days];
    const rows: React.ReactNode[] = [];
    let cells: React.ReactNode[] = [];

    totalCells.forEach((cell, i) => {
      if (i % 7 !== 0) {
        cells.push(cell);
      } else {
        rows.push(cells);
        cells = [];
        cells.push(cell);
      }
      if (i === totalCells.length - 1) {
        rows.push(cells);
      }
    });
    console.log(rows)
    console.log("space between")
    console.log(cells)
    return rows.map((row, i) => (
      <button key={`row-${i}`} className="calendar-row" onClick={renderConfirm}>
        {row}
      </button>
    ));
  };
  
  const renderConfirm=()=>{
    alert('hello')
    return(
    <div>
      <input type='text' name = 'notes' value="" onChange={e=>setNotes(e.target.value)}/>
      <label>reptation</label>
      <select id="repeated" name='repeated'>
        <option value='once'>once</option>
        <option value='weekly'>weekly</option>
        <option value='monthly'>monthly</option>
        <option value='yearly'>yearly</option>
      </select>
    </div>
    )
  }

  return (
    <div className="calendar">
      {/* Header */}
      <div className="calendar-header">
        <button onClick={goToPreviousMonth}>‹</button>
        <h2>
          {getMonthName(month)} {year}
        </h2>
        <button onClick={goToNextMonth}>›</button>
      </div>

      {/* Weekdays */}
      <div className="days-row ">
        {getWeekdayNames().map((day) => (
          <div className="day-name" key={day}>{day}</div>
        ))}
      </div>

      {/* Days */}
      <div className="calendar-body">{renderCells()}</div>
      {/* <div className="days-row">
        {days.map(({ date, isCurrentMonth }) => (
          <button
            key={date.toISOString()}
            className={`day-number 
              ${!isCurrentMonth ? 'text-gray-400' : ''}
              ${isSameDay(date, selectedDate) ? 'bg-blue-500 text-white' : 'hover:bg-blue-100'}`}
            onClick={() => setSelectedDate(new Date(date))}
          >
            {date.getDate()}
          </button>
        ))}
      </div> */}
    </div>
  );
};

export default Calendar;
